// interface.ts
import { ReactNode } from 'react';

/**
 * R组件Props类型定义
 */
export interface RProps {
  /**
   * 子元素内容
   */
  children?: ReactNode;

  /**
   * 自定义类名
   */
  className?: string;

  /**
   * 背景颜色
   */
  backgroundColor?: string;

  /**
   * 圆角大小
   */
  borderRadius?: number;

  /**
   * 是否显示阴影效果
   */
  shadow?: boolean;

  /**
   * 内边距
   */
  padding?: string | number;

  /**
   * 外边距
   */
  margin?: string | number;

  /**
   * 卡片标题
   */
  title?: string;

  /**
   * 卡片标题国际化Key
   */
  titleKey?: string;

  /**
   * 点击事件回调
   */
  onClick?: () => void;

  /**
   * 鼠标移入事件回调
   */
  onMouseEnter?: () => void;

  /**
   * 鼠标移出事件回调
   */
  onMouseLeave?: () => void;

  /**
   * 是否显示为暗色模式
   */
  darkMode?: boolean;

  /**
   * 卡片类型
   */
  type?: 'default' | 'primary' | 'secondary';
}

/**
 * 样式配置类型
 */
export interface RStyleConfig {
  /**
   * 主色调
   */
  primaryColor?: string;

  /**
   * 次色调
   */
  secondaryColor?: string;

  /**
   * 文本颜色
   */
  textColor?: string;

  /**
   * 暗色模式背景色
   */
  darkBackground?: string;

  /**
   * 暗色模式文本颜色
   */
  darkTextColor?: string;
}

// R.tsx
import React, { useMemo } from 'react';
import { useTranslation } from 'react-i18next';
import { RProps } from './interface';
import { Card } from 'antd-mobile';
import classNames from 'classnames';
import './styles.css';

/**
 * 可重用的响应式卡片组件
 */
const R: React.FC<RProps> = ({
  children,
  className = '',
  backgroundColor,
  borderRadius = 16,
  shadow = true,
  padding = '24px 16px',
  margin,
  title,
  titleKey,
  onClick,
  onMouseEnter,
  onMouseLeave,
  darkMode = false,
  type = 'default',
}) => {
  const { t } = useTranslation();

  // 合并默认样式和传入的类名
  const classes = classNames(
    'r-component',
    {
      'r-shadow': shadow,
      'r-dark': darkMode,
      [`r-type-${type}`]: type !== 'default',
    },
    className
  );

  // 计算最终显示的标题
  const displayTitle = useMemo(() => {
    if (titleKey) return t(titleKey);
    return title;
  }, [title, titleKey, t]);

  // 动态样式
  const style = {
    ...(backgroundColor && { backgroundColor }),
    borderRadius: typeof borderRadius === 'number' ? `${borderRadius}px` : borderRadius,
    padding: typeof padding === 'number' ? `${padding}px` : padding,
    ...(margin && { margin: typeof margin === 'number' ? `${margin}px` : margin }),
  };

  return (
    <Card
      className={classes}
      style={style}
      onClick={onClick}
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
    >
      {displayTitle && <div className="r-title">{displayTitle}</div>}
      <div className="r-content">{children}</div>
    </Card>
  );
};

export default R;

// index.ts
import R from './R';
export type { RProps, RStyleConfig } from './interface';

export default R;

// helpers.ts
import { RStyleConfig } from './interface';

/**
 * 生成组件CSS变量
 */
export const generateCSSVariables = (config: RStyleConfig): Record<string, string> => {
  return {
    '--r-primary-color': config.primaryColor || '#3727AB',
    '--r-secondary-color': config.secondaryColor || '#17CE92',
    '--r-text-color': config.textColor || '#333333',
    '--r-dark-background': config.darkBackground || '#191919',
    '--r-dark-text-color': config.darkTextColor || '#FFFFFF',
  };
};

/**
 * 检查是否支持CSS变量
 */
export const supportsCSSVariables = (): boolean => {
  return typeof window !== 'undefined' && 
         window.CSS && 
         window.CSS.supports && 
         window.CSS.supports('--a', '0');
};

/* styles.css */
.r-component {
  transition: all 0.3s ease;
  box-sizing: border-box;
  position: relative;
  background-color: var(--r-background, #FFFFFF);
  color: var(--r-text-color, #333333);
}

.r-shadow {
  box-shadow: 0 4px 34px rgba(0, 0, 0, 0.05);
}

.r-title {
  font-weight: 600;
  font-size: 1.2rem;
  margin-bottom: 12px;
  color: inherit;
}

.r-content {
  width: 100%;
}

/* 暗色模式 */
.r-dark {
  background-color: var(--r-dark-background, #191919);
  color: var(--r-dark-text-color, #FFFFFF);
}

/* 主题类型 */
.r-type-primary {
  background-color: var(--r-primary-color, #3727AB);
  color: var(--r-dark-text-color, #FFFFFF);
}

.r-type-secondary {
  background-color: var(--r-secondary-color, #17CE92);
  color: var(--r-dark-text-color, #FFFFFF);
}

/* 响应式设计 */
@media (max-width: 768px) {
  .r-component {
    padding: 16px 12px !important;
    border-radius: 12px !important;
  }

  .r-title {
    font-size: 1.1rem;
  }
}

@media (max-width: 480px) {
  .r-component {
    padding: 12px 8px !important;
    border-radius: 8px !important;
  }
}
